    private void AgregarUsuario(String usuario, String nombre, String paterno, String materno, String password) {
        PreparedStatement ps;
        Statement st;
        ResultSet rs;
        String sql = "INSERT INTO usuarios VALUES(?,?,?,?,?)";
        try {
            st = cn.createStatement();
            rs = st.executeQuery("SELECT * FROM usuarios WHERE id_usuario LIKE '" + usuario + "'");
            if (rs.next()) {
                JOptionPane.showMessageDialog(null, "EL ID DE USUARIO YA SE ENCUENTRA REGISTRADO", "ERROR", JOptionPane.ERROR_MESSAGE);
            } else {
                ps = cn.prepareStatement(sql);
                ps.setString(1, usuario);
                ps.setString(2, nombre);
                ps.setString(3, paterno);
                ps.setString(4, materno);
                ps.setString(5, password);
                ps.executeUpdate();
                JOptionPane.showMessageDialog(null, "USUARIO AGREGADO CORRECTAMENTE");
                Limpiar();
            }

        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "REGISTRO NO EXITOSO" + e, "ERROR", JOptionPane.ERROR_MESSAGE);
        }
    }

    private void Limpiar() {
        txt_confirm_password.setText("");
        txt_materno.setText("");
        txt_nombre.setText("");
        txt_password.setText("");
        txt_paterno.setText("");
        txt_usuario.setText("");
    }

